        this.$stateParams = $stateParams;
        this.$filter = $filter;
        this.view = view;
        this.data = data;
        this.entity = view.getEntity();
        this.title = view.title();
        this.description = view.description();
        this.progression = progression;
        this.RetrieveQueries = RetrieveQueries;
